contracts/interfaces/modules/base/IViewM.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { IModule } from "./IModule.sol";

/// @notice View Module Interface
/// View modules typically are read-only modules that are responsible for displaying
/// IP-related data in various ways to meet different needs. For instance,
/// they can display simple/base/core metadata, book specific metadata, license details metadata,
/// or even IP graph data for the same IPAccount using different View Modules.
/// This module offers flexibility in selecting which data to display and how to present it.
/// @dev View Module can read data from IPAccount and from multiple namespaces to combine data for display.
interface IViewModule is IModule {
    /// @notice check whether the view module is supported for the given IP account
    function isSupported(address ipAccount) external returns (bool);
}

contracts/interfaces/modules/dispute/IDi.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

/// @title Dispute Module Interface
interface IDisputeModule {
    /// @notice Dispute struct
    /// @param targetIpId The ipId that is the target of the dispute
    /// @param disputeInitiator The address of the dispute initiator
    /// @param disputeTimestamp The timestamp of the dispute
    /// @param arbitrationPolicy The address of the arbitration policy
    /// @param disputeEvidenceHash The hash pointing to the dispute evidence
    /// @param targetTag The target tag of the dispute
    /// @param currentTag The current tag of the dispute
    /// @param infringerDisputeId The infringer dispute id
    struct Dispute {
        address targetIpId;
        address disputeInitiator;
        uint256 disputeTimestamp;
        address arbitrationPolicy;
        bytes32 disputeEvidenceHash;
        bytes32 targetTag;
        bytes32 currentTag;
        uint256 infringerDisputeId;
    }

    /// @notice Event emitted when a dispute tag whitelist status is updated
    /// @param tag The dispute tag
    /// @param allowed Indicates if the dispute tag is whitelisted
    event TagWhitelistUpdated(bytes32 tag, bool allowed);

    /// @notice Event emitted when an arbitration policy whitelist status is updated
    /// @param arbitrationPolicy The address of the arbitration policy
    /// @param allowed Indicates if the arbitration policy is whitelisted
    event ArbitrationPolicyWhitelistUpdated(address arbitrationPolicy, bool allowed);

    /// @notice Event emitted when an arbitration relayer address is updated
    /// @param arbitrationPolicy The address of the arbitration policy
    /// @param arbitrationRelayer The address of the arbitration relayer
    event ArbitrationRelayerUpdated(address arbitrationPolicy, address arbitrationRelayer);

    /// @notice Event emitted when the base arbitration policy is set
    /// @param arbitrationPolicy The address of the arbitration policy
    event DefaultArbitrationPolicyUpdated(address arbitrationPolicy);

    /// @notice Event emitted when the arbitration policy cooldown is updated
    /// @param cooldown The cooldown in seconds
    event ArbitrationPolicyCooldownUpdated(uint256 cooldown);

    /// @notice Event emitted when an arbitration policy is set for an ipId
    /// @param ipId The ipId address
    /// @param arbitrationPolicy The address of the arbitration policy
    /// @param nextArbitrationUpdateTimestamp The timestamp of the next arbitration update
    event ArbitrationPolicySet(address ipId, address arbitrationPolicy, uint256 nextArbitrationUpdateTimestamp);

    /// @notice Event emitted when a dispute is raised
    /// @param disputeId The dispute id
    /// @param targetIpId The ipId that is the target of the dispute
    /// @param caller The address of the caller
    /// @param disputeInitiator The address of the dispute initiator
    /// @param disputeTimestamp The timestamp of the dispute
    /// @param arbitrationPolicy The address of the arbitration policy
    /// @param disputeEvidenceHash The hash pointing to the dispute evidence
    /// @param targetTag The target tag of the dispute
    /// @param data Custom data adjusted to each policy
    event DisputeRaised(
        uint256 disputeId,
        address targetIpId,
        address caller,
        address disputeInitiator,
        uint256 disputeTimestamp,
        address arbitrationPolicy,
        bytes32 disputeEvidenceHash,
        bytes32 targetTag,
        bytes data
    );

    /// @notice Event emitted when a dispute judgement is set
    /// @param disputeId The dispute id
    /// @param decision The decision of the dispute
    /// @param data Custom data adjusted to each policy
    event DisputeJudgementSet(uint256 disputeId, bool decision, bytes data);

    /// @notice Event emitted when a dispute is cancelled
    /// @param disputeId The dispute id
    /// @param data Custom data adjusted to each policy
    event DisputeCancelled(uint256 disputeId, bytes data);

    /// @notice Event emitted when a derivative is tagged if a parent infringed
    /// or a group ip is taggedif a group member infringed
    /// @param disputeId The dispute id
    /// @param infringingIpId The ipId which infringed
    /// @param ipIdToTag The ipId which was tagged
    /// @param infringerDisputeId The dispute id in which infringement was found
    /// @param tag The tag of the dispute applied to the ipIdToTag
    /// @param disputeTimestamp The timestamp of the dispute
    event IpTaggedOnRelatedIpInfringement(
        uint256 disputeId,
        address infringingIpId,
        address ipIdToTag,
        uint256 infringerDisputeId,
        bytes32 tag,
        uint256 disputeTimestamp
    );

    /// @notice Event emitted when a dispute is resolved
    /// @param disputeId The dispute id
    /// @param data Custom data adjusted to each policy
    event DisputeResolved(uint256 disputeId, bytes data);

    /// @notice Dispute ID counter
    function disputeCounter() external view returns (uint256);

    /// @notice The address of the base arbitration policy
    function baseArbitrationPolicy() external view returns (address);

    /// @notice Returns the dispute information for a given dispute id
    /// @param disputeId The dispute id
    /// @return targetIpId The ipId that is the target of the dispute
    /// @return disputeInitiator The address of the dispute initiator
    /// @return disputeTimestamp The timestamp of the dispute
    /// @return arbitrationPolicy The address of the arbitration policy
    /// @return disputeEvidenceHash The link of the dispute summary
    /// @return targetTag The target tag of the dispute
    /// @return currentTag The current tag of the dispute
    /// @return infringerDisputeId The infringer dispute id
    function disputes(
        uint256 disputeId
    )
        external
        view
        returns (
            address targetIpId,
            address disputeInitiator,
            uint256 disputeTimestamp,
            address arbitrationPolicy,
            bytes32 disputeEvidenceHash,
            bytes32 targetTag,
            bytes32 currentTag,
            uint256 infringerDisputeId
        );

    /// @notice Indicates if a dispute tag is whitelisted
    /// @param tag The dispute tag
    /// @return allowed Indicates if the dispute tag is whitelisted
    function isWhitelistedDisputeTag(bytes32 tag) external view returns (bool allowed);

    /// @notice Indicates if an arbitration policy is whitelisted
    /// @param arbitrationPolicy The address of the arbitration policy
    /// @return allowed Indicates if the arbitration policy is whitelisted
    function isWhitelistedArbitrationPolicy(address arbitrationPolicy) external view returns (bool allowed);

    /// @notice Returns the arbitration relayer for a given arbitration policy
    /// @param arbitrationPolicy The address of the arbitration policy
    /// @return arbitrationRelayer The address of the arbitration relayer
    function arbitrationRelayer(address arbitrationPolicy) external view returns (address arbitrationRelayer);

    /// @notice Arbitration policy for a given ipId
    /// @param ipId The ipId
    /// @return policy The address of the arbitration policy
    function arbitrationPolicies(address ipId) external view returns (address policy);

    /// @notice Whitelists a dispute tag
    /// @param tag The dispute tag
    /// @param allowed Indicates if the dispute tag is whitelisted or not
    function whitelistDisputeTag(bytes32 tag, bool allowed) external;

    /// @notice Whitelists an arbitration policy
    /// @param arbitrationPolicy The address of the arbitration policy
    /// @param allowed Indicates if the arbitration policy is whitelisted or not
    function whitelistArbitrationPolicy(address arbitrationPolicy, bool allowed) external;

    /// @notice Sets the arbitration relayer for a given arbitration policy
    /// @param arbitrationPolicy The address of the arbitration policy
    /// @param arbPolicyRelayer The address of the arbitration relayer
    function setArbitrationRelayer(address arbitrationPolicy, address arbPolicyRelayer) external;

    /// @notice Sets the base arbitration policy
    /// @param arbitrationPolicy The address of the arbitration policy
    function setBaseArbitrationPolicy(address arbitrationPolicy) external;

    /// @notice Sets the arbitration policy cooldown
    /// @param cooldown The cooldown in seconds
    function setArbitrationPolicyCooldown(uint256 cooldown) external;

    /// @notice Sets the arbitration policy for an ipId
    /// @param ipId The ipId
    /// @param arbitrationPolicy The address of the arbitration policy
    function setArbitrationPolicy(address ipId, address arbitrationPolicy) external;

    /// @notice Raises a dispute on a given ipId
    /// @param targetIpId The ipId that is the target of the dispute
    /// @param disputeEvidenceHash The hash pointing to the dispute evidence
    /// @param targetTag The target tag of the dispute
    /// @param data The data to raise a dispute
    /// @return disputeId The id of the newly raised dispute
    function raiseDispute(
        address targetIpId,
        bytes32 disputeEvidenceHash,
        bytes32 targetTag,
        bytes calldata data
    ) external returns (uint256 disputeId);

    /// @notice Sets the dispute judgement on a given dispute. Only whitelisted arbitration relayers can call to judge.
    /// @param disputeId The dispute id
    /// @param decision The decision of the dispute
    /// @param data The data to set the dispute judgement
    function setDisputeJudgement(uint256 disputeId, bool decision, bytes calldata data) external;

    /// @notice Cancels an ongoing dispute
    /// @param disputeId The dispute id
    /// @param data The data to cancel the dispute
    function cancelDispute(uint256 disputeId, bytes calldata data) external;

    /// @notice Tags a derivative if a parent has been tagged with an infringement tag
    /// or a group ip if a group member has been tagged with an infringement tag
    /// @param ipIdToTag The ipId to tag
    /// @param infringerDisputeId The dispute id that tagged the related infringing ipId
    function tagIfRelatedIpInfringed(address ipIdToTag, uint256 infringerDisputeId) external;

    /// @notice Resolves a dispute after it has been judged
    /// @param disputeId The dispute
    /// @param data The data to resolve the dispute
    function resolveDispute(uint256 disputeId, bytes calldata data) external;

    /// @notice Updates the active arbitration policy for a given ipId
    /// @param ipId The ipId
    /// @return arbitrationPolicy The address of the arbitration policy
    function updateActiveArbitrationPolicy(address ipId) external returns (address arbitrationPolicy);

    /// @notice Returns true if the ipId is tagged with any tag (meaning at least one dispute went through)
    /// @param ipId The ipId
    /// @return isTagged True if the ipId is tagged
    function isIpTagged(address ipId) external view returns (bool);
}

contracts/interfaces/modules/dispute/pol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

/// @title Arbitration Policy Interface
interface IArbitrationPolicy {
    /// @notice Executes custom logic on raising dispute
    /// @dev Enforced to be only callable by the DisputeModule
    /// @param caller Address of the caller
    /// @param disputeInitiator Address of the dispute initiator
    /// @param targetIpId The ipId that is the target of the dispute
    /// @param disputeEvidenceHash The hash pointing to the dispute evidence
    /// @param targetTag The target tag of the dispute
    /// @param disputeId The dispute id
    /// @param data The arbitrary data used to raise the dispute
    function onRaiseDispute(
        address caller,
        address disputeInitiator,
        address targetIpId,
        bytes32 disputeEvidenceHash,
        bytes32 targetTag,
        uint256 disputeId,
        bytes calldata data
    ) external;

    /// @notice Executes custom logic on disputing judgement
    /// @dev Enforced to be only callable by the DisputeModule
    /// @param disputeId The dispute id
    /// @param decision The decision of the dispute
    /// @param data The arbitrary data used to set the dispute judgement
    function onDisputeJudgement(uint256 disputeId, bool decision, bytes calldata data) external;

    /// @notice Executes custom logic on disputing cancel
    /// @dev Enforced to be only callable by the DisputeModule
    /// @param caller Address of the caller
    /// @param disputeId The dispute id
    /// @param data The arbitrary data used to cancel the dispute
    function onDisputeCancel(address caller, uint256 disputeId, bytes calldata data) external;

    /// @notice Executes custom logic on resolving dispute
    /// @dev Enforced to be only callable by the DisputeModule
    /// @param caller Address of the caller
    /// @param disputeId The dispute id
    /// @param data The arbitrary data used to resolve the dispute
    function onResolveDispute(address caller, uint256 disputeId, bytes calldata data) external;
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { IArbitrationPolicy } from "../IArbitrationPolicy.sol";
import { IOOV3Callbacks } from "./IOOV3Callbacks.sol";

/// @title Arbitration Policy UMA Interface
interface IArbitrationPolicyUMA is IArbitrationPolicy, IOOV3Callbacks {
    /// @notice Emitted when the OOV3 address is set
    /// @param oov3 The address of the OOV3
    event OOV3Set(address oov3);

    /// @notice Emitted when liveness is set
    /// @param minLiveness The minimum liveness value
    /// @param maxLiveness The maximum liveness value
    /// @param ipOwnerTimePercent The percentage of liveness time the IP owner has priority to respond to a dispute
    event LivenessSet(uint64 minLiveness, uint64 maxLiveness, uint32 ipOwnerTimePercent);

    /// @notice Emitted when max bond is set
    /// @param token The token address
    /// @param maxBond The maximum bond value
    event MaxBondSet(address token, uint256 maxBond);

    /// @notice Emitted when a dispute is raised
    /// @param disputeId The dispute id
    /// @param assertionId The assertion id
    /// @param disputeInitiator The address that initiated the dispute
    /// @param liveness The liveness time
    /// @param currency The bond currency
    /// @param bond The bond size
    event DisputeRaisedUMA(
        uint256 disputeId,
        bytes32 assertionId,
        address disputeInitiator,
        uint64 liveness,
        address currency,
        uint256 bond
    );

    /// @notice Emitted when an assertion is disputed
    /// @param disputeId The dispute id
    /// @param assertionId The assertion id
    /// @param counterEvidenceHash The counter evidence hash
    event AssertionDisputed(uint256 disputeId, bytes32 assertionId, bytes32 counterEvidenceHash);

    /// @notice Sets the OOV3 address
    /// @param oov3 The address of the OOV3
    function setOOV3(address oov3) external;

    /// @notice Sets the liveness for UMA disputes
    /// @param minLiveness The minimum liveness value
    /// @param maxLiveness The maximum liveness value
    /// @param ipOwnerTimePercent The percentage of liveness time the IP owner has priority to respond to a dispute
    function setLiveness(uint64 minLiveness, uint64 maxLiveness, uint32 ipOwnerTimePercent) external;

    /// @notice Sets the max bond for UMA disputes
    /// @param token The token address
    /// @param maxBond The maximum bond value
    function setMaxBond(address token, uint256 maxBond) external;

    /// @notice Allows the IP that was targeted with a dispute to dispute the assertion while providing counter evidence
    /// @param assertionId The identifier of the assertion that was disputed
    /// @param counterEvidenceHash The hash of the counter evidence
    function disputeAssertion(bytes32 assertionId, bytes32 counterEvidenceHash) external;

    /// @notice Returns the minimum liveness for UMA disputes
    function minLiveness() external view returns (uint64);

    /// @notice Returns the maximum liveness for UMA disputes
    function maxLiveness() external view returns (uint64);

    /// @notice Returns the percentage of liveness time the IP owner has priority to respond to a dispute
    function ipOwnerTimePercent() external view returns (uint32);

    /// @notice Returns the percentage of liveness time the IP owner has priority to respond to a dispute
    /// for a given dispute id
    function ipOwnerTimePercents(uint256 disputeId) external view returns (uint32);

    /// @notice Returns the OOV3 address
    function oov3() external view returns (address);

    /// @notice Returns the maximum bond for a given token for UMA disputes
    /// @param token The token address
    function maxBonds(address token) external view returns (uint256);

    /// @notice Returns the assertion id for a given dispute id
    /// @param disputeId The dispute id
    function disputeIdToAssertionId(uint256 disputeId) external view returns (bytes32);

    /// @notice Returns the dispute id for a given assertion id
    /// @param assertionId The assertion id
    function assertionIdToDisputeId(bytes32 assertionId) external view returns (uint256);
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/// @title IOOV3 Interface
interface IOOV3 {
    struct EscalationManagerSettings {
        bool arbitrateViaEscalationManager;
        bool discardOracle;
        bool validateDisputers;
        address assertingCaller;
        address escalationManager;
    }

    struct Assertion {
        EscalationManagerSettings escalationManagerSettings;
        address asserter;
        uint64 assertionTime;
        bool settled;
        IERC20 currency;
        uint64 expirationTime;
        bool settlementResolution;
        bytes32 domainId;
        bytes32 identifier;
        uint256 bond;
        address callbackRecipient;
        address disputer;
    }

    function assertTruth(
        bytes memory claim,
        address asserter,
        address callbackRecipient,
        address escalationManager,
        uint64 liveness,
        IERC20 currency,
        uint256 bond,
        bytes32 identifier,
        bytes32 domainId
    ) external returns (bytes32 assertionId);

    function disputeAssertion(bytes32 assertionId, address disputer) external;

    function settleAssertion(bytes32 assertionId) external;

    function getAssertion(bytes32 assertionId) external view returns (Assertion memory);

    function stampAssertion(bytes32 assertionId) external view returns (bytes memory);

    function burnedBondPercentage() external view returns (uint256);

    function getMinimumBond(address currency) external view returns (uint256);
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

/// @title IOOV3 Callbacks Interface
interface IOOV3Callbacks {
    function assertionResolvedCallback(bytes32 assertionId, bool assertedTruthfully) external;

    function assertionDisputedCallback(bytes32 assertionId) external;
}

contracts/interfaces/modules/grouping/IG.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { IModule } from "../base/IModule.sol";

/// @title IGroupingModule
/// @notice This interface defines the entry point for users to manage group in the Story Protocol.
/// It defines the workflow of grouping actions and coordinates among all grouping components.
/// The Grouping Module is responsible for adding ip to group, removing ip from group and claiming reward.
interface IGroupingModule is IModule {
    /// @notice Emitted when a group is registered.
    /// @param groupId The address of the group.
    /// @param groupPool The address of the group pool.
    event IPGroupRegistered(address indexed groupId, address indexed groupPool);

    /// @notice Emitted when added ip to group.
    /// @param groupId The address of the group.
    /// @param ipIds The IP ID.
    event AddedIpToGroup(address indexed groupId, address[] ipIds);

    /// @notice Emitted when removed ip from group.
    /// @param groupId The address of the group.
    /// @param ipIds The IP ID.
    event RemovedIpFromGroup(address indexed groupId, address[] ipIds);

    /// @notice Emitted when claimed reward.
    /// @param groupId The address of the group.
    /// @param token The address of the token.
    /// @param ipId The IP ID.
    /// @param amount The amount of reward.
    event ClaimedReward(address indexed groupId, address indexed token, address[] ipId, uint256[] amount);

    /// @notice Emitted when collected royalties into the group pool.
    /// @param groupId The address of the group.
    /// @param token The address of the token.
    /// @param pool The address of the pool.
    /// @param amount The amount of reward.
    event CollectedRoyaltiesToGroupPool(
        address indexed groupId,
        address indexed token,
        address indexed pool,
        uint256 amount
    );

    /// @notice Registers a Group IPA.
    /// @param groupPool The address of the group pool.
    /// @return groupId The address of the newly registered Group IPA.
    function registerGroup(address groupPool) external returns (address groupId);

    /// @notice Whitelists a group reward pool.
    /// @param rewardPool The address of the group reward pool.
    /// @param allowed Whether the group reward pool is whitelisted.
    function whitelistGroupRewardPool(address rewardPool, bool allowed) external;

    /// @notice Adds IP to group.
    /// the function must be called by the Group IP owner or an authorized operator.
    /// @param groupIpId The address of the group IP.
    /// @param ipIds The IP IDs.
    /// @param maxAllowedRewardShare The maximum reward share percentage that can be allocated to each member IP.
    function addIp(address groupIpId, address[] calldata ipIds, uint256 maxAllowedRewardShare) external;

    /// @notice Removes IP from group.
    /// the function must be called by the Group IP owner or an authorized operator.
    /// @param groupIpId The address of the group IP.
    /// @param ipIds The IP IDs.
    function removeIp(address groupIpId, address[] calldata ipIds) external;

    /// @notice Claims reward.
    /// @param groupId The address of the group.
    /// @param token The address of the token.
    /// @param ipIds The IP IDs.
    function claimReward(address groupId, address token, address[] calldata ipIds) external;

    /// @notice Collects royalties into the pool, making them claimable by group member IPs.
    /// @param groupId The address of the group.
    /// @param token The address of the token.
    function collectRoyalties(address groupId, address token) external returns (uint256 royalties);

    /// @notice Returns the available reward for each IP in the group.
    /// @param groupId The address of the group.
    /// @param token The address of the token.
    /// @param ipIds The IP IDs.
    /// @return The rewards for each IP.
    function getClaimableReward(
        address groupId,
        address token,
        address[] calldata ipIds
    ) external view returns (uint256[] memory);
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

/// @title IGroupingPolicy
/// @notice Interface for grouping policies
interface IGroupRewardPool {
    /// @notice Distributes rewards to the given IP accounts in pool
    /// @param groupId The group ID
    /// @param token The reward tokens
    /// @param ipIds The IP IDs
    function distributeRewards(
        address groupId,
        address token,
        address[] calldata ipIds
    ) external returns (uint256[] memory rewards);

    /// @notice Deposits reward to the group pool directly
    /// @param groupId The group ID
    /// @param token The reward token
    /// @param amount The amount of reward
    function depositReward(address groupId, address token, uint256 amount) external;

    /// @notice Adds an IP to the group pool
    /// @param groupId The group ID
    /// @param ipId The IP ID
    /// @param minimumGroupRewardShare The minimum group reward share the IP expects to be added to the group
    /// @return totalGroupRewardShare The total group reward share after adding the IP
    function addIp(
        address groupId,
        address ipId,
        uint256 minimumGroupRewardShare
    ) external returns (uint256 totalGroupRewardShare);

    /// @notice Removes an IP from the group pool
    /// @param groupId The group ID
    /// @param ipId The IP ID
    function removeIp(address groupId, address ipId) external;

    /// @notice Returns the available reward for each IP in the group
    /// @param groupId The group ID
    /// @param token The reward token
    /// @param ipIds The IP IDs
    /// @return The rewards for each IP
    function getAvailableReward(
        address groupId,
        address token,
        address[] calldata ipIds
    ) external view returns (uint256[] memory);
}

contracts/interfaces/modules/licensing/I.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { IERC165 } from "@openzeppelin/contracts/interfaces/IERC165.sol";

/// @title ILicenseTemplate
/// @notice This interface defines the methods for a License Template.
/// A License Template is responsible for defining a template of license terms that allow users to create
/// licenses based on the template terms.
/// The License Template contract is also responsible for registering, storing, verifying,
/// and displaying license terms registered with the License Template.
/// Anyone can implement a License Template and register it into the Story Protocol.
/// @dev The License Template should assign a unique ID to each license terms registered.
interface ILicenseTemplate is IERC165 {
    /// @notice Emitted when a new license terms is registered.
    /// @param licenseTermsId The ID of the license terms.
    /// @param licenseTemplate The address of the license template.
    /// @param licenseTerms The data of the license.
    event LicenseTermsRegistered(uint256 indexed licenseTermsId, address indexed licenseTemplate, bytes licenseTerms);

    /// @notice Returns the name of the license template.
    /// @return The name of the license template.
    function name() external view returns (string memory);

    /// @notice Converts the license terms to a JSON string which will be part of the metadata of license token.
    /// @dev the json will be part of metadata as attributes return by tokenURI() license token,
    /// hence the json format should follow the common NFT metadata standard.
    /// @param licenseTermsId The ID of the license terms.
    /// @return The JSON string of the license terms.
    function toJson(uint256 licenseTermsId) external view returns (string memory);

    /// @notice Returns the metadata URI of the license template.
    /// @return The metadata URI of the license template.
    function getMetadataURI() external view returns (string memory);

    /// @notice Returns the URI of the license terms.
    /// @param licenseTermsId The ID of the license terms.
    /// @return The URI of the license terms.
    function getLicenseTermsURI(uint256 licenseTermsId) external view returns (string memory);

    /// @notice Returns the total number of registered license terms.
    /// @return The total number of registered license terms.
    function totalRegisteredLicenseTerms() external view returns (uint256);

    /// @notice Checks if a license terms exists.
    /// @param licenseTermsId The ID of the license terms.
    /// @return True if the license terms exists, false otherwise.
    function exists(uint256 licenseTermsId) external view returns (bool);

    /// @notice Checks if a license terms is transferable.
    /// @param licenseTermsId The ID of the license terms.
    /// @return True if the license terms is transferable, false otherwise.
    function isLicenseTransferable(uint256 licenseTermsId) external view returns (bool);

    /// @notice Returns the earliest expiration time among the given license terms.
    /// @param start The start time to calculate the expiration time.
    /// @param licenseTermsIds The IDs of the license terms.
    /// @return The earliest expiration time.
    function getEarlierExpireTime(uint256[] calldata licenseTermsIds, uint256 start) external view returns (uint256);

    /// @notice Returns the expiration time of a license terms.
    /// @param start The start time.
    /// @param licenseTermsId The ID of the license terms.
    /// @return The expiration time.
    function getExpireTime(uint256 licenseTermsId, uint256 start) external view returns (uint256);

    /// @notice Returns the royalty policy of a license terms.
    /// @dev All License Templates should implement this method.
    /// The royalty policy is used to calculate royalties and pay minting license fee.
    /// Should return address(0) if the license template does not support a royalty policy or
    /// the license term does set RoyaltyPolicy.
    /// @param licenseTermsId The ID of the license terms.
    /// @return royaltyPolicy The address of the royalty policy specified for the license terms.
    /// @return royaltyPercent The percentage of the royalty.
    /// @return mintingLicenseFee The fee for minting a license.
    /// @return currencyToken The address of the ERC20 token, used for minting license fee and royalties.
    /// the currency token will used for pay for license token minting fee and royalties.
    function getRoyaltyPolicy(
        uint256 licenseTermsId
    )
        external
        view
        returns (address royaltyPolicy, uint32 royaltyPercent, uint256 mintingLicenseFee, address currencyToken);

    /// @notice Verifies the minting of a license token.
    /// @dev the function will be called by the LicensingModule when minting a license token to
    /// verify the minting is whether allowed by the license terms.
    /// @param licenseTermsId The ID of the license terms.
    /// @param licensee The address of the licensee who will receive the license token.
    /// @param licensorIpId The IP ID of the licensor who attached the license terms minting the license token.
    /// @param amount The amount of licenses to mint.
    /// @return True if the minting is verified, false otherwise.
    function verifyMintLicenseToken(
        uint256 licenseTermsId,
        address licensee,
        address licensorIpId,
        uint256 amount
    ) external returns (bool);

    /// @notice Verifies the registration of a derivative.
    /// @dev This function is invoked by the LicensingModule during the registration of a derivative work
    //// to ensure compliance with the parent intellectual property's licensing terms.
    /// It verifies whether the derivative's registration is permitted under those terms.
    /// @param childIpId The IP ID of the derivative.
    /// @param parentIpId The IP ID of the parent.
    /// @param licenseTermsId The ID of the license terms.
    /// @param licensee The address of the licensee.
    /// @return True if the registration is verified, false otherwise.
    function verifyRegisterDerivative(
        address childIpId,
        address parentIpId,
        uint256 licenseTermsId,
        address licensee
    ) external returns (bool);

    /// @notice Verifies if the licenses are compatible.
    /// @dev This function is called by the LicensingModule to verify license compatibility
    /// when registering a derivative IP to multiple parent IPs.
    /// It ensures that the licenses of all parent IPs are compatible with each other during the registration process.
    /// @param licenseTermsIds The IDs of the license terms.
    /// @return True if the licenses are compatible, false otherwise.
    function verifyCompatibleLicenses(uint256[] calldata licenseTermsIds) external view returns (bool);

    /// @notice Verifies the registration of a derivative for all parent IPs.
    /// @dev This function is called by the LicensingModule to verify licenses for registering a derivative IP
    /// to multiple parent IPs.
    /// the function will verify the derivative for each parent IP's license and
    /// also verify all licenses are compatible.
    /// @param childIpId The IP ID of the derivative.
    /// @param parentIpId The IP IDs of the parents.
    /// @param licenseTermsIds The IDs of the license terms.
    /// @param caller The address initiating the derivative registration.
    /// @return True if the registration is verified, false otherwise.
    function verifyRegisterDerivativeForAllParents(
        address childIpId,
        address[] calldata parentIpId,
        uint256[] calldata licenseTermsIds,
        address caller
    ) external returns (bool);

    /// @notice Verifies if the royalty percentage defined in the licenseTermsId can be overridden with
    /// the given newRoyaltyPercent.
    /// @param licenseTermsId The ID of the license terms.
    /// @param newRoyaltyPercent The new royalty percentage.
    /// @return True if the royalty percentage can be overridden, false otherwise.
    function canOverrideRoyaltyPercent(uint256 licenseTermsId, uint32 newRoyaltyPercent) external view returns (bool);

    /// @notice queries if the derivative registration is allowed under the license terms.
    /// @param licenseTermsId The ID of the license terms.
    /// @return True if the derivative registration is allowed, false otherwise.
    function allowDerivativeRegistration(uint256 licenseTermsId) external view returns (bool);

    /// @notice check if the license terms support associate with group IP assets
    /// @param licenseTermsId The ID of the license terms.
    /// @return True if the license terms support associate with group IP assets, false otherwise.
    function canAttachToGroupIp(uint256 licenseTermsId) external view returns (bool);
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { IModule } from "../base/IModule.sol";

/// @title ILicensingHook
/// @notice This interface defines the hook functions that are called by the LicensingModule when
/// executing licensing functions.
/// IP owners can configure the hook to a specific license terms or all licenses of an IP Asset.
/// @dev Developers can create a contract that implements this interface to implement various checks
/// and determine the minting price.
/// Initially, the hooks must be registered as modules in the ModuleRegistry.
interface ILicensingHook is IModule {
    /// @notice This function is called when the LicensingModule mints license tokens.
    /// @dev The hook can be used to implement various checks and determine the minting price.
    /// The hook should revert if the minting is not allowed.
    /// @param caller The address of the caller who calling the mintLicenseTokens() function.
    /// @param licensorIpId The ID of licensor IP from which issue the license tokens.
    /// @param licenseTemplate The address of the license template.
    /// @param licenseTermsId The ID of the license terms within the license template,
    /// which is used to mint license tokens.
    /// @param amount The amount of license tokens to mint.
    /// @param receiver The address of the receiver who receive the license tokens.
    /// @param hookData The data to be used by the licensing hook.
    /// @return totalMintingFee The total minting fee to be paid when minting amount of license tokens.
    function beforeMintLicenseTokens(
        address caller,
        address licensorIpId,
        address licenseTemplate,
        uint256 licenseTermsId,
        uint256 amount,
        address receiver,
        bytes calldata hookData
    ) external returns (uint256 totalMintingFee);

    /// @notice This function is called before finalizing LicensingModule.registerDerivative(), after calling
    /// LicenseRegistry.registerDerivative().
    /// @dev The hook can be used to implement various checks and determine the minting price.
    /// The hook should revert if the registering of derivative is not allowed.
    /// @param childIpId The derivative IP ID.
    /// @param parentIpId The parent IP ID.
    /// @param licenseTemplate The address of the license template.
    /// @param licenseTermsId The ID of the license terms within the license template.
    /// @param hookData The data to be used by the licensing hook.
    /// @return mintingFee The minting fee to be paid when register child IP to the parent IP as derivative.
    function beforeRegisterDerivative(
        address caller,
        address childIpId,
        address parentIpId,
        address licenseTemplate,
        uint256 licenseTermsId,
        bytes calldata hookData
    ) external returns (uint256 mintingFee);

    /// @notice This function is called when the LicensingModule calculates/predict the minting fee for license tokens.
    /// @dev The hook should guarantee the minting fee calculation is correct and return the minting fee which is
    /// the exact same amount with returned by beforeMintLicenseTokens().
    /// The hook should revert if the minting fee calculation is not allowed.
    /// @param caller The address of the caller who calling the mintLicenseTokens() function.
    /// @param licensorIpId The ID of licensor IP from which issue the license tokens.
    /// @param licenseTemplate The address of the license template.
    /// @param licenseTermsId The ID of the license terms within the license template,
    /// which is used to mint license tokens.
    /// @param amount The amount of license tokens to mint.
    /// @param receiver The address of the receiver who receive the license tokens.
    /// @param hookData The data to be used by the licensing hook.
    /// @return totalMintingFee The total minting fee to be paid when minting amount of license tokens.
    function calculateMintingFee(
        address caller,
        address licensorIpId,
        address licenseTemplate,
        uint256 licenseTermsId,
        uint256 amount,
        address receiver,
        bytes calldata hookData
    ) external view returns (uint256 totalMintingFee);
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { IModule } from "../base/IModule.sol";
import { Licensing } from "../../../lib/Licensing.sol";

/// @title ILicensingModule
/// @notice This interface defines the entry point for users to manage licenses in the Story Protocol.
/// It defines the workflow of license actions and coordinates among all license components and dependent components,
/// like RoyaltyModule.
/// The Licensing Module is responsible for attaching license terms to an IP, minting license tokens,
/// and registering derivatives.
interface ILicensingModule is IModule {
    /// @notice Emitted when new license terms are attached to an IP.
    /// @param caller The address of the caller.
    /// @param ipId The IP ID.
    /// @param licenseTemplate The address of the license template.
    /// @param licenseTermsId The ID of the license terms in the license template.
    event LicenseTermsAttached(
        address indexed caller,
        address indexed ipId,
        address licenseTemplate,
        uint256 licenseTermsId
    );

    /// @notice Emitted when license tokens are minted.
    /// @param caller The address of the caller.
    /// @param licensorIpId The parent IP ID.
    /// @param licenseTemplate The address of the license template.
    /// @param licenseTermsId The ID of the license terms.
    /// @param amount The amount of license tokens minted.
    /// @param receiver The address of the receiver.
    /// @param startLicenseTokenId The start ID of the minted license tokens.
    event LicenseTokensMinted(
        address indexed caller,
        address indexed licensorIpId,
        address licenseTemplate,
        uint256 indexed licenseTermsId,
        uint256 amount,
        address receiver,
        uint256 startLicenseTokenId
    );

    /// @notice Emitted when a derivative IP is registered.
    /// @param caller The address of the caller.
    /// @param childIpId The derivative IP ID.
    /// @param licenseTokenIds The IDs of the license tokens.
    /// @param parentIpIds The parent IP IDs.
    /// @param licenseTermsIds The IDs of the license terms.
    /// @param licenseTemplate The address of the license template.
    event DerivativeRegistered(
        address indexed caller,
        address indexed childIpId,
        uint256[] licenseTokenIds,
        address[] parentIpIds,
        uint256[] licenseTermsIds,
        address licenseTemplate
    );

    /// @notice Attaches default license terms to an IP.
    /// the function must be called by the IP owner or an authorized operator.
    /// @param ipId The IP ID.
    function attachDefaultLicenseTerms(address ipId) external;

    /// @notice Attaches license terms to an IP.
    /// the function must be called by the IP owner or an authorized operator.
    /// @param ipId The IP ID.
    /// @param licenseTemplate The address of the license template.
    /// @param licenseTermsId The ID of the license terms.
    function attachLicenseTerms(address ipId, address licenseTemplate, uint256 licenseTermsId) external;

    /// @notice Mints license tokens for the license terms attached to an IP.
    /// The license tokens are minted to the receiver.
    /// The license terms must be attached to the IP before calling this function.
    /// But it can mint license token of default license terms without attaching the default license terms,
    /// since it is attached to all IPs by default.
    /// IP owners can mint license tokens for their IPs for arbitrary license terms
    /// without attaching the license terms to IP.
    /// It might require the caller pay the minting fee, depending on the license terms or configured by the iP owner.
    /// The minting fee is paid in the minting fee token specified in the license terms or configured by the IP owner.
    /// IP owners can configure the minting fee of their IPs or
    /// configure the minting fee module to determine the minting fee.
    /// IP owners can configure the receiver check module to determine the receiver of the minted license tokens.
    /// @param licensorIpId The licensor IP ID.
    /// @param licenseTemplate The address of the license template.
    /// @param licenseTermsId The ID of the license terms within the license template.
    /// @param amount The amount of license tokens to mint.
    /// @param receiver The address of the receiver.
    /// @param royaltyContext The context of the royalty.
    /// @param maxMintingFee The maximum minting fee that the caller is willing to pay. if set to 0 then no limit.
    /// @param maxRevenueShare The maximum revenue share percentage allowed for minting the License Tokens.
    /// @return startLicenseTokenId The start ID of the minted license tokens.
    function mintLicenseTokens(
        address licensorIpId,
        address licenseTemplate,
        uint256 licenseTermsId,
        uint256 amount,
        address receiver,
        bytes calldata royaltyContext,
        uint256 maxMintingFee,
        uint32 maxRevenueShare
    ) external returns (uint256 startLicenseTokenId);

    /// @notice Registers a derivative directly with parent IP's license terms, without needing license tokens,
    /// and attaches the license terms of the parent IPs to the derivative IP.
    /// The license terms must be attached to the parent IP before calling this function.
    /// All IPs attached default license terms by default.
    /// The derivative IP owner must be the caller or an authorized operator.
    /// @param childIpId The derivative IP ID.
    /// @param parentIpIds The parent IP IDs.
    /// @param licenseTermsIds The IDs of the license terms that the parent IP supports.
    /// @param licenseTemplate The address of the license template of the license terms Ids.
    /// @param royaltyContext The context of the royalty.
    /// @param maxMintingFee The maximum minting fee that the caller is willing to pay. if set to 0 then no limit.
    /// @param maxRts The maximum number of royalty tokens that can be distributed to the external royalty policies.
    /// @param maxRevenueShare The maximum revenue share percentage allowed for minting the License Tokens.
    function registerDerivative(
        address childIpId,
        address[] calldata parentIpIds,
        uint256[] calldata licenseTermsIds,
        address licenseTemplate,
        bytes calldata royaltyContext,
        uint256 maxMintingFee,
        uint32 maxRts,
        uint32 maxRevenueShare
    ) external;

    /// @notice Registers a derivative with license tokens.
    /// the derivative IP is registered with license tokens minted from the parent IP's license terms.
    /// the license terms of the parent IPs issued with license tokens are attached to the derivative IP.
    /// the caller must be the derivative IP owner or an authorized operator.
    /// @param childIpId The derivative IP ID.
    /// @param licenseTokenIds The IDs of the license tokens.
    /// @param royaltyContext The context of the royalty.
    /// @param maxRts The maximum number of royalty tokens that can be distributed to the external royalty policies.
    function registerDerivativeWithLicenseTokens(
        address childIpId,
        uint256[] calldata licenseTokenIds,
        bytes calldata royaltyContext,
        uint32 maxRts
    ) external;

    /// @notice Sets the licensing configuration for a specific license terms of an IP.
    /// If both licenseTemplate and licenseTermsId are not specified then the licensing config apply
    /// to all licenses of given IP.
    /// @param ipId The address of the IP for which the configuration is being set.
    /// @param licenseTemplate The address of the license template used.
    /// If not specified, the configuration applies to all licenses.
    /// @param licenseTermsId The ID of the license terms within the license template.
    /// If not specified, the configuration applies to all licenses.
    /// @param licensingConfig The licensing configuration for the license.
    function setLicensingConfig(
        address ipId,
        address licenseTemplate,
        uint256 licenseTermsId,
        Licensing.LicensingConfig memory licensingConfig
    ) external;

    /// @notice pre-compute the minting license fee for the given IP and license terms.
    /// the function can be used to calculate the minting license fee before minting license tokens.
    /// @param licensorIpId The IP ID of the licensor.
    /// @param licenseTemplate The address of the license template.
    /// @param licenseTermsId The ID of the license terms.
    /// @param amount The amount of license tokens to mint.
    /// @param receiver The address of the receiver.
    /// @param royaltyContext The context of the royalty.
    /// @return currencyToken The address of the ERC20 token used for minting license fee.
    /// @return tokenAmount The amount of the currency token to be paid for minting license tokens.
    function predictMintingLicenseFee(
        address licensorIpId,
        address licenseTemplate,
        uint256 licenseTermsId,
        uint256 amount,
        address receiver,
        bytes calldata royaltyContext
    ) external view returns (address currencyToken, uint256 tokenAmount);
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { ILicenseTemplate } from "../../../interfaces/modules/licensing/ILicenseTemplate.sol";

/// @notice This struct defines the terms for a Programmable IP License (PIL).
/// These terms can be attached to IP Assets. The legal document of the PIL can be found in this repository.
/// @param transferable Indicates whether the license is transferable or not.
/// @param royaltyPolicy The address of the royalty policy contract which required to StoryProtocol in advance.
/// @param mintingFee The fee to be paid when minting a license.
/// @param expiration The expiration period of the license.
/// @param commercialUse Indicates whether the work can be used commercially or not.
/// @param commercialAttribution whether attribution is required when reproducing the work commercially or not.
/// @param commercializerChecker commercializers that are allowed to commercially exploit the work. If zero address,
/// then no restrictions is enforced.
/// @param commercializerCheckerData The data to be passed to the commercializer checker contract.
/// @param commercialRevShare Percentage of revenue that must be shared with the licensor.
/// @param commercialRevCeiling The maximum revenue that can be generated from the commercial use of the work.
/// @param derivativesAllowed Indicates whether the licensee can create derivatives of his work or not.
/// @param derivativesAttribution Indicates whether attribution is required for derivatives of the work or not.
/// @param derivativesApproval Indicates whether the licensor must approve derivatives of the work before they can be
/// linked to the licensor IP ID or not.
/// @param derivativesReciprocal Indicates whether the licensee must license derivatives of the work under the
/// same terms or not.
/// @param derivativeRevCeiling The maximum revenue that can be generated from the derivative use of the work.
/// @param currency The ERC20 token to be used to pay the minting fee. the token must be registered in story protocol.
/// @param uri The URI of the license terms, which can be used to fetch the offchain license terms.
struct PILTerms {
    bool transferable;
    address royaltyPolicy;
    uint256 defaultMintingFee;
    uint256 expiration;
    bool commercialUse;
    bool commercialAttribution;
    address commercializerChecker;
    bytes commercializerCheckerData;
    uint32 commercialRevShare;
    uint256 commercialRevCeiling;
    bool derivativesAllowed;
    bool derivativesAttribution;
    bool derivativesApproval;
    bool derivativesReciprocal;
    uint256 derivativeRevCeiling;
    address currency;
    string uri;
}

/// @title IPILicenseTemplate
/// @notice This interface defines the methods for a Programmable IP License (PIL) template.
/// The PIL template is used to generate PIL terms that can be attached to IP Assets.
/// The legal document of the PIL can be found in this repository.
interface IPILicenseTemplate is ILicenseTemplate {
    /// @notice Registers new license terms.
    /// @param terms The PILTerms to register.
    /// @return selectedLicenseTermsId The ID of the newly registered license terms.
    function registerLicenseTerms(PILTerms calldata terms) external returns (uint256 selectedLicenseTermsId);

    /// @notice Gets the ID of the given license terms.
    /// @param terms The PILTerms to get the ID for.
    /// @return selectedLicenseTermsId The ID of the given license terms.
    function getLicenseTermsId(PILTerms calldata terms) external view returns (uint256 selectedLicenseTermsId);

    /// @notice Gets license terms of the given ID.
    /// @param selectedLicenseTermsId The ID of the license terms.
    /// @return terms The PILTerms associate with the given ID.
    function getLicenseTerms(uint256 selectedLicenseTermsId) external view returns (PILTerms memory terms);
}

contracts/interfaces/modules/metadata/IC.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { IModule } from "../../../../contracts/interfaces/modules/base/IModule.sol";

/// @title CoreMetadataModule
/// @notice Manages the core metadata for IP assets within the Story Protocol.
/// @dev This contract allows setting and updating core metadata attributes for IP assets.
interface ICoreMetadataModule is IModule {
    /// @notice Emitted when the nftTokenURI for an IP asset is set.
    event NFTTokenURISet(address indexed ipId, string nftTokenURI, bytes32 nftMetadataHash);

    /// @notice Emitted when the metadataURI for an IP asset is set.
    event MetadataURISet(address indexed ipId, string metadataURI, bytes32 metadataHash);

    /// @notice Emitted when all metadata for an IP asset are frozen.
    event MetadataFrozen(address indexed ipId);

    /// @notice Update the nftTokenURI for an IP asset,
    /// by retrieve the latest TokenURI from IP NFT to which the IP Asset bound.
    /// @dev Will revert if IP asset's metadata is frozen.
    /// @param ipId The address of the IP asset.
    /// @param nftMetadataHash A bytes32 hash representing the metadata of the NFT.
    /// This metadata is associated with the IP Asset and is accessible via the NFT's TokenURI.
    /// Use bytes32(0) to indicate that the metadata is not available.
    function updateNftTokenURI(address ipId, bytes32 nftMetadataHash) external;

    /// @notice Sets the metadataURI for an IP asset.
    /// @dev Will revert if IP asset's metadata is frozen.
    /// @param ipId The address of the IP asset.
    /// @param metadataURI The metadataURI to set for the IP asset.
    /// @param metadataHash The hash of metadata at metadataURI.
    /// Use bytes32(0) to indicate that the metadata is not available.
    function setMetadataURI(address ipId, string memory metadataURI, bytes32 metadataHash) external;

    /// @notice Sets all core metadata for an IP asset.
    /// @dev Will revert if IP asset's metadata is frozen.
    /// @param ipId The address of the IP asset.
    /// @param metadataURI The metadataURI to set for the IP asset.
    /// @param metadataHash The hash of metadata at metadataURI.
    /// Use bytes32(0) to indicate that the metadata is not available.
    /// @param nftMetadataHash A bytes32 hash representing the metadata of the NFT.
    /// This metadata is associated with the IP Asset and is accessible via the NFT's TokenURI.
    /// Use bytes32(0) to indicate that the metadata is not available.
    function setAll(address ipId, string memory metadataURI, bytes32 metadataHash, bytes32 nftMetadataHash) external;

    /// @notice make all metadata of the IP Asset immutable.
    /// @param ipId The address of the IP asset.
    function freezeMetadata(address ipId) external;

    /// @notice Check if the metadata of the IP Asset is immutable.
    /// @param ipId The address of the IP asset.
    function isMetadataFrozen(address ipId) external view returns (bool);
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { IViewModule } from "../base/IViewModule.sol";

/// @title CoreMetadataViewModule
/// @notice This view module provides getter functions to access all core metadata
///         and generate json string of all core metadata returned by tokenURI().
///         The view module consolidates core metadata for IPAssets from both IPAssetRegistry and CoreMetadataModule.
/// @dev The "name" from CoreMetadataModule overrides the "name" from IPAssetRegistry if set.
interface ICoreMetadataViewModule is IViewModule {
    /// @notice Core metadata struct for IPAssets.
    struct CoreMetadata {
        string nftTokenURI;
        bytes32 nftMetadataHash;
        string metadataURI;
        bytes32 metadataHash;
        uint256 registrationDate;
        address owner;
    }

    /// @notice Retrieves the metadataURI of the IPAsset from CoreMetadataModule.
    /// @param ipId The address of the IPAsset.
    /// @return The metadataURI of the IPAsset.
    function getMetadataURI(address ipId) external view returns (string memory);

    /// @notice Retrieves the metadata hash of the IPAsset from CoreMetadataModule.
    /// @param ipId The address of the IPAsset.
    /// @return The metadata hash of the IPAsset.
    function getMetadataHash(address ipId) external view returns (bytes32);

    /// @notice Retrieves the registration date of the IPAsset from IPAssetRegistry.
    /// @param ipId The address of the IPAsset.
    /// @return The registration date of the IPAsset.
    function getRegistrationDate(address ipId) external view returns (uint256);

    /// @notice Retrieves the TokenURI of the NFT to which IP Asset bound.
    /// @param ipId The address of the IPAsset.
    /// @return The NFT TokenURI of the IPAsset.
    function getNftTokenURI(address ipId) external view returns (string memory);

    /// @notice Retrieves the metadata hash of the NFT to which IP Asset bound.
    /// @param ipId The address of the IPAsset.
    /// @return The NFT metadata hash of the IPAsset.
    function getNftMetadataHash(address ipId) external view returns (bytes32);

    /// @notice Retrieves the owner of the IPAsset.
    /// @param ipId The address of the IPAsset.
    /// @return The address of the owner of the IPAsset.
    function getOwner(address ipId) external view returns (address);

    /// @notice Retrieves all core metadata of the IPAsset.
    /// @param ipId The address of the IPAsset.
    /// @return The CoreMetadata struct of the IPAsset.
    function getCoreMetadata(address ipId) external view returns (CoreMetadata memory);

    /// @notice Generates a JSON string formatted according to the standard NFT metadata schema for the IPAsset,
    ///        including all relevant metadata fields.
    /// @dev This function consolidates metadata from both IPAssetRegistry
    ///      and CoreMetadataModule, with "name" from CoreMetadataModule taking precedence.
    /// @param ipId The address of the IPAsset.
    /// @return A JSON string representing all metadata of the IPAsset.
    function getJsonString(address ipId) external view returns (string memory);
}

contracts/interfaces/modules/royalty/IRo.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { IModule } from "../../modules/base/IModule.sol";

/// @title RoyaltyModule interface
interface IRoyaltyModule is IModule {
    /// @notice Event emitted when the treasury is set
    /// @param treasury The address of the treasury
    event TreasurySet(address treasury);

    /// @notice Event emitted when the royalty fee is set
    /// @param royaltyFeePercent The royalty fee percent
    event RoyaltyFeePercentSet(uint256 royaltyFeePercent);

    /// @notice Event emitted when a royalty policy is whitelisted
    /// @param royaltyPolicy The address of the royalty policy
    /// @param allowed Indicates if the royalty policy is whitelisted or not
    event RoyaltyPolicyWhitelistUpdated(address royaltyPolicy, bool allowed);

    /// @notice Event emitted when a royalty token is whitelisted
    /// @param token The address of the royalty token
    /// @param allowed Indicates if the royalty token is whitelisted or not
    event RoyaltyTokenWhitelistUpdated(address token, bool allowed);

    /// @notice Event emitted when royalties are paid
    /// @param receiverIpId The ID of IP asset that receives the royalties
    /// @param payerIpId The ID of IP asset that pays the royalties
    /// @param sender The address that pays the royalties on behalf of the payer ID of IP asset
    /// @param token The token that is used to pay the royalties
    /// @param amount The amount that is paid
    /// @param amountAfterFee The amount after fee
    event RoyaltyPaid(
        address receiverIpId,
        address payerIpId,
        address sender,
        address token,
        uint256 amount,
        uint256 amountAfterFee
    );

    /// @notice Event emitted when the license minting fee is paid
    /// @param receiverIpId The ipId that receives the royalties
    /// @param payerAddress The address that pays the royalties
    /// @param token The token that is used to pay the royalties
    /// @param amount The amount that is paid
    /// @param amountAfterFee The amount after fee
    event LicenseMintingFeePaid(
        address receiverIpId,
        address payerAddress,
        address token,
        uint256 amount,
        uint256 amountAfterFee
    );

    /// @notice Event emitted when a royalty policy is registered
    /// @param externalRoyaltyPolicy The address of the external royalty policy
    event ExternalRoyaltyPolicyRegistered(address externalRoyaltyPolicy);

    /// @notice Event emitted when the Royalty limits are updated
    /// @param accumulatedRoyaltyPoliciesLimit The maximum number of accumulated royalty policies an IP asset can have
    event RoyaltyLimitsUpdated(uint256 accumulatedRoyaltyPoliciesLimit);

    /// @notice Event emitted when a license is minted
    /// @param ipId The ipId whose license is being minted (licensor)
    /// @param royaltyPolicy The royalty policy address of the license being minted
    /// @param licensePercent The license percentage of the license being minted
    /// @param externalData The external data custom to the royalty policy being minted
    event LicensedWithRoyalty(address ipId, address royaltyPolicy, uint32 licensePercent, bytes externalData);

    /// @notice Event emitted when an IP asset is linked to parents
    /// @param ipId The children ipId that is being linked to parents
    /// @param parentIpIds The parent ipIds that the children ipId is being linked to
    /// @param licenseRoyaltyPolicies The royalty policies of the each parent license being used to link
    /// @param licensesPercent The license percentage of the licenses of each parent being used to link
    /// @param externalData The external data custom to each the royalty policy being used to link
    event LinkedToParents(
        address ipId,
        address[] parentIpIds,
        address[] licenseRoyaltyPolicies,
        uint32[] licensesPercent,
        bytes externalData
    );

    /// @notice Event emitted when an IP royalty vault is deployed
    /// @param ipId The ipId of IP asset
    /// @param ipRoyaltyVault The address of the royalty vault
    event IpRoyaltyVaultDeployed(address ipId, address ipRoyaltyVault);

    /// @notice Sets the treasury address
    /// @dev Enforced to be only callable by the protocol admin
    /// @param treasury The address of the treasury
    function setTreasury(address treasury) external;

    /// @notice Sets the royalty fee percentage
    /// @dev Enforced to be only callable by the protocol admin
    /// @param royaltyFeePercent The royalty fee percentage
    function setRoyaltyFeePercent(uint32 royaltyFeePercent) external;

    /// @notice Sets the royalty limits
    /// @dev Enforced to be only callable by the protocol admin
    /// @param accumulatedRoyaltyPoliciesLimit The maximum number of accumulated royalty policies an IP asset can have
    function setRoyaltyLimits(uint256 accumulatedRoyaltyPoliciesLimit) external;

    /// @notice Whitelist a royalty policy
    /// @dev Enforced to be only callable by the protocol admin
    /// @param royaltyPolicy The address of the royalty policy
    /// @param allowed Indicates if the royalty policy is whitelisted or not
    function whitelistRoyaltyPolicy(address royaltyPolicy, bool allowed) external;

    /// @notice Whitelist a royalty token
    /// @dev Enforced to be only callable by the protocol admin
    /// @param token The token address
    /// @param allowed Indicates if the token is whitelisted or not
    function whitelistRoyaltyToken(address token, bool allowed) external;

    /// @notice Registers an external royalty policy
    /// @param externalRoyaltyPolicy The address of the external royalty policy
    function registerExternalRoyaltyPolicy(address externalRoyaltyPolicy) external;

    /// @notice Deploys a royalty vault for a given IP asset
    /// @param ipId The ID of the IP asset
    /// @return ipRoyaltyVault The address of the deployed royalty vault
    function deployVault(address ipId) external returns (address ipRoyaltyVault);

    /// @notice Executes royalty related logic on license minting
    /// @dev Enforced to be only callable by LicensingModule
    /// @param ipId The ipId whose license is being minted (licensor)
    /// @param royaltyPolicy The royalty policy address of the license being minted
    /// @param licensePercent The license percentage of the license being minted
    /// @param externalData The external data custom to each the royalty policy
    function onLicenseMinting(
        address ipId,
        address royaltyPolicy,
        uint32 licensePercent,
        bytes calldata externalData
    ) external;

    /// @notice Executes royalty related logic on linking to parents
    /// @dev Enforced to be only callable by LicensingModule
    /// @param ipId The children ipId that is being linked to parents
    /// @param parentIpIds The parent ipIds that the children ipId is being linked to
    /// @param licensesPercent The license percentage of the licenses being minted
    /// @param externalData The external data custom to each the royalty policy
    /// @param maxRts The maximum number of royalty tokens that can be distributed to the external royalty policies
    function onLinkToParents(
        address ipId,
        address[] calldata parentIpIds,
        address[] calldata licenseRoyaltyPolicies,
        uint32[] calldata licensesPercent,
        bytes calldata externalData,
        uint32 maxRts
    ) external;

    /// @notice Allows the function caller to pay royalties to the receiver IP asset on behalf of the payer IP asset.
    /// @param receiverIpId The ipId that receives the royalties
    /// @param payerIpId The ipId that pays the royalties
    /// @param token The token to use to pay the royalties
    /// @param amount The amount to pay
    function payRoyaltyOnBehalf(address receiverIpId, address payerIpId, address token, uint256 amount) external;

    /// @notice Allows to pay the minting fee for a license
    /// @param receiverIpId The ipId that receives the royalties
    /// @param payerAddress The address that pays the royalties
    /// @param token The token to use to pay the royalties
    /// @param amount The amount to pay
    function payLicenseMintingFee(address receiverIpId, address payerAddress, address token, uint256 amount) external;

    /// @notice Indicates if an IP asset has a specific ancestor IP asset
    /// @param ipId The ID of IP asset
    /// @param ancestorIpId The ID of the ancestor IP asset
    function hasAncestorIp(address ipId, address ancestorIpId) external returns (bool);

    /// @notice Returns the maximum percentage - represents 100%
    function maxPercent() external pure returns (uint32);

    /// @notice Returns the treasury address
    function treasury() external view returns (address);

    /// @notice Returns the royalty fee percentage
    function royaltyFeePercent() external view returns (uint32);

    /// @notice Returns the maximum number of parents
    function maxParents() external view returns (uint256);

    /// @notice Returns the maximum number of total ancestors
    function maxAncestors() external view returns (uint256);

    /// @notice Returns the maximum number of accumulated royalty policies an IP asset can have
    function maxAccumulatedRoyaltyPolicies() external view returns (uint256);

    /// @notice Indicates if a royalty policy is whitelisted
    /// @param royaltyPolicy The address of the royalty policy
    /// @return isWhitelisted True if the royalty policy is whitelisted
    function isWhitelistedRoyaltyPolicy(address royaltyPolicy) external view returns (bool);

    /// @notice Indicates if an external royalty policy is registered
    /// @param externalRoyaltyPolicy The address of the external royalty policy
    /// @return isRegistered True if the external royalty policy is registered
    function isRegisteredExternalRoyaltyPolicy(address externalRoyaltyPolicy) external view returns (bool);

    /// @notice Indicates if a royalty token is whitelisted
    /// @param token The address of the royalty token
    /// @return isWhitelisted True if the royalty token is whitelisted
    function isWhitelistedRoyaltyToken(address token) external view returns (bool);

    /// @notice Indicates if an address is a royalty vault
    /// @param ipRoyaltyVault The address to check
    /// @return isIpRoyaltyVault True if the address is a royalty vault
    function isIpRoyaltyVault(address ipRoyaltyVault) external view returns (bool);

    /// @notice Indicates the royalty vault for a given IP asset
    /// @param ipId The ID of IP asset
    function ipRoyaltyVaults(address ipId) external view returns (address);

    /// @notice Returns the global royalty stack for whitelisted royalty policies and a given IP asset
    /// @param ipId The ID of IP asset
    function globalRoyaltyStack(address ipId) external view returns (uint32);

    /// @notice Returns the accumulated royalty policies for a given IP asset
    /// @param ipId The ID of IP asset
    function accumulatedRoyaltyPolicies(address ipId) external view returns (address[] memory);

    /// @notice Returns the total lifetime revenue tokens received for a given IP asset
    /// @param ipId The ID of IP asset
    /// @param token The token address
    function totalRevenueTokensReceived(address ipId, address token) external view returns (uint256);

    /// @notice Returns the total revenue tokens received by a given IP asset while a given royalty
    /// policy is whitelisted. If a royalty policy is whitelisted since the beginning then the value will be equal
    /// to the total revenue tokens received over the lifetime of the IP asset. But whenever a payment is made to an
    /// IP asset while a royalty policy is blacklisted then that payment will not be accounted for that royalty policy.
    /// @param ipId The ID of IP asset
    /// @param token The token address
    /// @param royaltyPolicy The royalty policy address
    function totalRevenueTokensAccounted(
        address ipId,
        address token,
        address royaltyPolicy
    ) external view returns (uint256);
}

