// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { IERC165 } from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import { IExternalRoyaltyPolicyBase } from "./IExternalRoyaltyPolicyBase.sol";

/// @title IExternalRoyaltyPolicy interface
interface IExternalRoyaltyPolicy is IExternalRoyaltyPolicyBase, IERC165 {}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

/// @title IExternalRoyaltyPolicyBase interface
interface IExternalRoyaltyPolicyBase {
    /// @notice Returns the amount of royalty tokens required to link a child to a given IP asset
    /// @param ipId The ipId of the IP asset
    /// @param licensePercent The percentage of the license
    /// @return The amount of royalty tokens required to link a child to a given IP asset
    function getPolicyRtsRequiredToLink(address ipId, uint32 licensePercent) external view returns (uint32);
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { IRoyaltyPolicy } from "../../../../interfaces/modules/royalty/policies/IRoyaltyPolicy.sol";

/// @title IGraphAwareRoyaltyPolicy interface
interface IGraphAwareRoyaltyPolicy is IRoyaltyPolicy {
    /// @notice Event emitted when revenue tokens are transferred to a vault from a royalty policy
    /// @param ipId The ipId of the IP asset
    /// @param ancestorIpId The ancestor ipId of the IP asset whose vault will receive revenue tokens
    /// @param token The address of the token that is transferred
    /// @param amount The amount of tokens transferred
    event RevenueTransferredToVault(address ipId, address ancestorIpId, address token, uint256 amount);

    /// @notice Transfers to vault an amount of revenue tokens
    /// @param ipId The ipId of the IP asset
    /// @param ancestorIpId The ancestor ipId of the IP asset
    /// @param token The token address to transfer
    /// @return The amount of revenue tokens transferred
    function transferToVault(address ipId, address ancestorIpId, address token) external returns (uint256);

    /// @notice Returns the royalty percentage between an IP asset and a given ancestor
    /// @param ipId The ipId to get the royalty for
    /// @param ancestorIpId The ancestor ipId to get the royalty for
    /// @return The royalty percentage between an IP asset and a given ancestor
    function getPolicyRoyalty(address ipId, address ancestorIpId) external returns (uint32);

    /// @notice Returns the total lifetime revenue tokens transferred to an ancestor's vault from a given IP asset
    /// @param ipId The ipId of the IP asset
    /// @param ancestorIpId The ancestor ipId of the IP asset
    /// @param token The token address to transfer
    /// @return The total lifetime revenue tokens transferred to an ancestor's vault from a given IP asset
    function getTransferredTokens(address ipId, address ancestorIpId, address token) external view returns (uint256);
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

/// @title IpRoyaltyVault interface
interface IIpRoyaltyVault {
    /// @notice Event emitted when a revenue token is added to a vault
    /// @param token The address of the revenue token
    /// @param amount The amount of revenue token added
    event RevenueTokenAddedToVault(address token, uint256 amount);

    /// @notice Event emitted when a revenue token is claimed
    /// @param claimer The address of the claimer
    /// @param token The revenue token claimed
    /// @param amount The amount of revenue token claimed
    event RevenueTokenClaimed(address claimer, address token, uint256 amount);

    /// @notice Event emitted when a revenue debt of a claimer is updated
    /// @param claimer The address of the claimer
    /// @param token The revenue token claimed
    /// @param revenueDebt The amount of revenue token claimed
    event RevenueDebtUpdated(address claimer, address token, int256 revenueDebt);

    /// @notice Initializer for this implementation contract
    /// @param name The name of the royalty token
    /// @param symbol The symbol of the royalty token
    /// @param supply The total supply of the royalty token
    /// @param ipIdAddress The ip id the royalty vault belongs to
    /// @param rtReceiver The address of the royalty token receiver
    function initialize(
        string memory name,
        string memory symbol,
        uint32 supply,
        address ipIdAddress,
        address rtReceiver
    ) external;

    /// @notice Updates the vault balance with the new amount of revenue token
    /// @param token The address of the revenue token
    /// @param amount The amount of revenue token to add
    /// @dev Only callable by the royalty module or whitelisted royalty policy
    function updateVaultBalance(address token, uint256 amount) external;

    /// @notice Allows token holders to claim revenue token
    /// @param claimer The address of the claimer
    /// @param token The revenue tokens to claim
    /// @return The amount of revenue tokens claimed
    function claimRevenueOnBehalf(address claimer, address token) external returns (uint256);

    /// @notice Allows token holders to claim a batch of revenue tokens
    /// @param claimer The address of the claimer
    /// @param tokenList The list of revenue tokens to claim
    /// @return The amount of revenue tokens claimed of each token
    function claimRevenueOnBehalfByTokenBatch(
        address claimer,
        address[] calldata tokenList
    ) external returns (uint256[] memory);

    /// @notice Allows to claim revenue tokens on behalf of the ip royalty vault
    /// @param tokenList The list of revenue tokens to claim
    /// @param targetIpId The target ip id to claim revenue tokens from
    function claimByTokenBatchAsSelf(address[] calldata tokenList, address targetIpId) external;

    /// @notice Get total amount of revenue token claimable by a token holder
    /// @param claimer The address of the token holder
    /// @param token The revenue token to claim
    /// @return The amount of revenue token claimable
    function claimableRevenue(address claimer, address token) external view returns (uint256);

    /// @notice The ip id to whom this royalty vault belongs to
    function ipId() external view returns (address);

    /// @notice The list of revenue tokens in the vault
    function tokens() external view returns (address[] memory);

    /// @notice The accumulated balance of revenue tokens in the vault
    /// @param token The revenue token to check
    /// @return The accumulated balance of revenue tokens in the vault
    function vaultAccBalances(address token) external view returns (uint256);

    /// @notice The revenue debt of the claimer, used to calculate the claimable revenue
    /// positive value means claimed need to deducted, negative value means claimable from vault
    /// @param claimer The address of the claimer
    /// @param token The revenue token to check
    /// @return The revenue debt of the claimer for the token
    function claimerRevenueDebt(address claimer, address token) external view returns (int256);
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import { IExternalRoyaltyPolicyBase } from "./IExternalRoyaltyPolicyBase.sol";

/// @title RoyaltyPolicy interface
interface IRoyaltyPolicy is IExternalRoyaltyPolicyBase {
    /// @notice Executes royalty related logic on minting a license
    /// @dev Enforced to be only callable by RoyaltyModule
    /// @param ipId The ipId whose license is being minted (licensor)
    /// @param licensePercent The license percentage of the license being minted
    /// @param externalData The external data custom to each the royalty policy
    function onLicenseMinting(address ipId, uint32 licensePercent, bytes calldata externalData) external;

    /// @notice Executes royalty related logic on linking to parents
    /// @dev Enforced to be only callable by RoyaltyModule
    /// @param ipId The children ipId that is being linked to parents
    /// @param parentIpIds The parent ipIds that the children ipId is being linked to
    /// @param licenseRoyaltyPolicies The royalty policies of the license
    /// @param licensesPercent The license percentages of the licenses being minted
    /// @param externalData The external data custom to each the royalty policy
    /// @return The royalty stack of the child ipId for a given royalty policy
    function onLinkToParents(
        address ipId,
        address[] calldata parentIpIds,
        address[] calldata licenseRoyaltyPolicies,
        uint32[] calldata licensesPercent,
        bytes calldata externalData
    ) external returns (uint32);

    /// @notice Returns the royalty stack for a given IP asset
    /// @param ipId The ID of the IP asset
    /// @return royaltyStack Sum of the royalty percentages to be paid to ancestors for a given royalty policy
    function getPolicyRoyaltyStack(address ipId) external view returns (uint32);

    /// @notice Returns the royalty policy support working with group
    /// @return True if the royalty policy support working with group otherwise false
    function isSupportGroup() external view returns (bool);
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

/// @title VaultController interface
interface IVaultController {
    /// @dev Set the ip royalty vault beacon
    /// @dev Enforced to be only callable by the protocol admin in governance
    /// @param beacon The ip royalty vault beacon address
    function setIpRoyaltyVaultBeacon(address beacon) external;

    /// @dev Upgrades the ip royalty vault beacon
    /// @dev Enforced to be only callable by the upgrader admin
    /// @param newVault The new ip royalty vault beacon address
    function upgradeVaults(address newVault) external;

    /// @notice Returns the ip royalty vault beacon
    /// @return ipRoyaltyVaultBeacon The ip royalty vault beacon address
    function ipRoyaltyVaultBeacon() external view returns (address);
}

contracts/interfaces/pause/IProtocolPaus.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

/// @title ProtocolPauseAdmin
/// @notice Contract that allows the pausing and unpausing of the protocol. It allows adding and removing
/// pausable contracts, which are contracts that implement the `IPausable` interface.
/// @dev The contract is restricted to be used only the admin role defined in the `AccessManaged` contract.
/// NOTE: If a contract is upgraded to remove the `IPausable` interface, it should be removed from the list of pausables
/// before the upgrade, otherwise pause() and unpause() will revert.
interface IProtocolPauseAdmin {
    /// @notice Emitted when a pausable contract is added.
    event PausableAdded(address indexed pausable);
    /// @notice Emitted when a pausable contract is removed.
    event PausableRemoved(address indexed pausable);
    /// @notice Emitted when the protocol is paused.
    event ProtocolPaused();
    /// @notice Emitted when the protocol is unpaused.
    event ProtocolUnpaused();

    /// @notice Adds a pausable contract to the list of pausables.
    function addPausable(address pausable) external;

    /// @notice Removes a pausable contract from the list of pausables.
    function removePausable(address pausable) external;

    /// @notice Pauses the protocol by calling the pause() function on all pausable contracts.
    function pause() external;

    /// @notice Unpauses the protocol by calling the unpause() function on all pausable contracts.
    function unpause() external;

    /// @notice Checks if a pausable contract is registered.
    function isPausableRegistered(address pausable) external view returns (bool);

    /// @notice Returns true if all the pausable contracts are paused.
    function isAllProtocolPaused() external view returns (bool);

    /// @notice Returns the list of pausable contracts.
    function pausables() external view returns (address[] memory);
}

